<?phpdefined('BASEPATH') OR exit('No direct script access allowed');class Credit_hist_model extends \CI_Model {        function get_hist_data( $limit, $offset, $order_by = null) {            $this -> db -> from('creditos_msjs cm');            $this -> db -> select(                        'cm.id, cm.detail, cm.hist_date, cm.hist_time, cm.enviados, cm.excluidos', FALSE  );                        if($order_by){                foreach ($order_by as $order => $tipo) {                    $this -> db -> order_by($order, $tipo);                        }            }                $this -> db -> limit($limit, $offset);                 $query = $this -> db -> get();                              $result = $query->result();            //            echo $this->db->last_query();                    return $result;                   }                 public function get_hist_count() {            $this -> db -> from('creditos_msjs');            return $this->db->count_all_results();            } }